// SearchNotes performs full-text search on notes
func (r *queryResolver) SearchNotes(ctx context.Context, query string) ([]*model.Note, error) {
	user, err := auth.RequireAuth(ctx)
	if err != nil {
		return nil, err
	}

	userID, err := uuid.Parse(user.ID)
	if err != nil {
		return nil, fmt.Errorf("invalid user ID: %w", err)
	}

	if query == "" {
		return []*model.Note{}, nil
	}

	// Use PostgreSQL full-text search with the search_vector column
	// Convert the query to tsquery format (handle multiple words)
	rows, err := r.DB.Query(`
		SELECT 
			id,
			user_id as userid,
			title,
			content,
			COALESCE(tags, '{}') as tags,
			linked_type as linkedtype,
			linked_id as linkedid,
			created_at as createdat,
			updated_at as updatedat,
			ts_rank(search_vector, to_tsquery('english', $2)) AS rank
		FROM notes
		WHERE user_id = $1 
		  AND search_vector @@ to_tsquery('english', $2)
		ORDER BY rank DESC, created_at DESC
		LIMIT 50
	`, userID, strings.ReplaceAll(query, " ", " & "))
	if err != nil {
		return nil, fmt.Errorf("failed to search notes: %w", err)
	}
	defer rows.Close()

	var notes []*model.Note
	for rows.Next() {
		var note model.Note
		var tags []string
		var rank float64

		err := rows.Scan(
			&note.ID,
			&note.UserID,
			&note.Title,
			&note.Content,
			pq.Array(&tags),
			&note.LinkedType,
			&note.LinkedID,
			&note.CreatedAt,
			&note.UpdatedAt,
			&rank,
		)
		if err != nil {
			return nil, fmt.Errorf("failed to scan note: %w", err)
		}

		note.Tags = tags
		notes = append(notes, &note)
	}

	if err = rows.Err(); err != nil {
		return nil, fmt.Errorf("error iterating notes: %w", err)
	}

	return notes, nil
}

